```tsx
■fn_start
// Step 4: Identify corrupted files and delete them
const corruptedFiles = fileContents.filter(
  (file) =>
    file.content.includes("CORRUPTED") ||
    file.content.includes("ERROR") ||
    file.content.includes("\0")
);

await Promise.all(
  corruptedFiles.map(async (file) => {
    await deleteFile({ filename: file.filename });
  })
);

// Prepare final result
const deletedFiles = corruptedFiles.map((file) => file.filename);
const totalWorkFiles = fileContents.length;
const corruptedCount = corruptedFiles.length;

return {
  action: 'done',
  value: { deletedFiles, totalWorkFiles, corruptedCount },
};
